INSERT INTO _exercise (id, difficulty, equipment, instructions, muscle, name, type) VALUES (2341, 'intermediate', 'dumbbell', 'Coaching Tips:  Stand in proper alignment with a dumbbell at eye level in front of you.  Draw your lower abdomen inward toward your spine (activating the deep stabilizing mechanism).  Rotate the dumbbell clockwise around your head, keep your core tight; go slowly as to not hit your head with the dumbbell.  Perform all repetitions in one direction then perform the same number in the other direction.  Be sure to keep your head and body straight.  Move the dumbbell around your head not your head and body around the dumbbell.', 'shoulders', 'Dumbbell Halo', 'strength');
